// import triangle from './img/triangle.svg';

function CoursesItem(props) {
  const {Name, CharCode, Value, Previous} = props;

  function triangle(Value, Previous){
    if(Value >= Previous){
      return <p>меньше</p>
    } else {
      return <p>больше</p>;
    }
  }
  return (
    <div className="courses__item">
      <p className="courses__item-title">{Name} ({CharCode})</p>
      <div className="courses__item-price">{Value} руб. {Previous} руб.
        <div className="courses__item-triangle">
          <div triangle={triangle(Value,Previous)}></div>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 46 46" ><path style={{fill: 'pink'}} d="M46 3.004 0 3l23.002 40z"/></svg>
        </div>
      </div>
    </div>
  );
}

export { CoursesItem };
