function CoursesItem(props) {
  const {Name, CharCode, Value, ID, Previous} = props;

  const triangle = (Value, Previous) =>{
    if(Value < Previous){
      return <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 46 46" ><path style={{fill: 'pink'}} d="M46 3.004 0 3l23.002 40z"/></svg>
    }
    else{
      return <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 46 46" ><path style={{fill: 'pink'}} d="M46 3.004 0 3l23.002 40z"/></svg>
    }
  }
  return (
    <div className="courses__item" id={ID} onClick={() => getMoreFv(Value, Previous)}>
      <p className="courses__item-title">{Name} ({CharCode})</p>
      <div className="courses__item-price">{Value} руб.
      <div>
        {triangle(Value, Previous)}
      </div>
        {/* <div className="courses__item-triangle">
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 46 46" ><path style={{fill: 'pink'}} d="M46 3.004 0 3l23.002 40z"/></svg>
        </div> */}
      </div>
    </div>
  );
}

export { CoursesItem };
