// import triangle from './img/triangle.svg';

function CoursesItem(props) {
  const {Name, CharCode, Value, Previous} = props;

  let triangle = null
  if(Value > Previous){
    triangle = <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 46 46" ><path style={{fill: 'pink'}} d="M46 3.004 0 3l23.002 40z"/></svg>
  } else {
    triangle = <p>vds</p>;
  }

  return (
    <div className="courses__item">
      <p className="courses__item-title">{Name} ({CharCode})</p>
      <div className="courses__item-price">{Value} руб. {Previous} руб.
        <div class="courses__item-triangle">
          {triangle}

        </div>
      </div>
    </div>
  );
}

export { CoursesItem };
