function CoursesItem(props) {
  const {Name, CharCode, Value} = props;

  return (
    <div className="courses__item">
      <p className="courses__item-title">{Name} ({CharCode})</p>
      <p className="courses__item-price">{Value} руб.
        <div class="courses__item-triangle">
          <svg width="230" height="140">
            <polygon points="5,135 115,5 225,135"
              fill="violet" stroke="purple" stroke-width="5"
            />
          </svg>
        </div>
      </p>
    </div>
  );
}

export { CoursesItem };
